import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import '../styles/Profile.css';
import { useState, useEffect } from 'react';

export const PROFILE_USER_QUERY = gql`
query profileUserQuery {
    Profile {
      id
      userId
      bio
      location
      website
    }
    me {
      id
      name
      createdAt
    }
}
`
;

function Profile() {
    let [profileImageBase64String, setProfileImageBase64String] = useState('');
    const {loading,error,data} = useQuery(PROFILE_USER_QUERY);
    useEffect(() => {
        if(loading ===false && error===undefined) {
            async function downloadProfileImage() {  
                try {
                    const response = await fetch('http://localhost:4001/downloadProfileImage', { // a promise that was returned by fetch() is resolved with the Response object which represents a response to a request
                        method: 'POST',
                        body: 'user_id_' + data.me.id + '_profile_image'
                    });
                    const responseData = await response.text(); // "text()" returns the "Promise<string>" type
                    setProfileImageBase64String('data:;base64,' + responseData);
                }
                catch(error) {
                    console.log('Error downloading the profile image',error);
                }
            }
            downloadProfileImage();
        }
    },[]);
  return (
    <div className='profile-container'>

    </div>
  );
}

export default Profile;
