import { gql, useMutation } from '@apollo/client';
import { ME_QUERY } from '../sites/Profile';
import { useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import Modal from 'react-modal';
import { customStyles } from '../styles/CustomModalStyles';

const CREATE_PROFILE_MUTATION = gql`
    mutation CreateProfile(
        $bio: String
        $location: String
        $website: String
    ) { CreateProfile(
            bio: $bio
            location: $location
            website: $website
        ) {
            id
        }
    }
`
;

const httpLink = new HttpLink({uri:'http://localhost:4000'});
const authLink = setContext(async(request,{headers}) => {
  const token = localStorage.getItem('token');
  return {
    ...headers,
    headers: {
      Authorization: token ? `Bearer ${token}` : null
    }
  }
});

const aLink = authLink.concat(httpLink as any);
const aClient = new ApolloClient({
  link: aLink as any,
  cache: new InMemoryCache()
});

interface ProfileValues {
    bio: string,
    location: string,
    website: string,
    avatar: string
}

function CreateProfile() {
    const [mutateCreateProfile] = useMutation(CREATE_PROFILE_MUTATION, { // returns a tuple but we only get one item
        refetchQueries: [{query: ME_QUERY}] // updates the client's cache with fresh data
    });
    const [modalIsOpen, setIsOpen] = useState(false);
    const initialValues : ProfileValues = {
        bio: "",
        location: "",
        website: "",
        avatar: ""
    };
    const openModal = () => {
        setIsOpen(true);
    };
    const closeModal = () => {
        setIsOpen(false);
    }
  return (
    <div>
        <button onClick={openModal} className='edit-button'>Create Profile</button>
        <Modal isOpen={modalIsOpen} // describes if the modal should be shown
            onRequestClose={closeModal} // function that will be run when the modal is requested to be closed
            contentLabel="Modal" // string for modal label
            style={customStyles} // styles for the modal
            ariaHideApp={false} // Boolean indicating if the appElement should be hidden
            >
        <Formik
            initialValues={initialValues}
            onSubmit={async (values,{setSubmitting}) => {
                setSubmitting(true);
                await mutateCreateProfile({ variables: values});
                setSubmitting(false);
                closeModal();
            }}>
                <Form>
                    <Field name="bio" type="text" as="textarea" placeholder="Bio" />
                    <ErrorMessage name="bio" component={'div'} />
                    <Field name="location" type="text" placeholder="Location" />
                    <ErrorMessage name="location" component={'div'} /> {/* component for rendering a message */}
                    <Field name="website" type="text" placeholder="Website" />
                    <ErrorMessage name="website" component={'div'} />
                    <button type="submit" className="login-button"><span>Create Profile</span></button>
                </Form>
        </Formik>
        </Modal>
    </div>
  )
}

export default CreateProfile;
