import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import '../styles/Profile.css';
import { useState, useEffect } from 'react';

export const PROFILE_USER_QUERY = gql`
query {
    Profile {
      id
      userId
      bio
      location
      website
    }
    me {
      id
      name
      createdAt
    }
}
`
;

export default function Profile() {
    let [profileImageBase64String, setProfileImageBase64String] = useState('');
    const {loading, error, data} = useQuery(PROFILE_USER_QUERY);
