import { gql, useMutation, useQuery } from '@apollo/client';
import { PROFILE_QUERY } from '../sites/Profile';
import { useState } from 'react';
import { Field, Form, Formik } from 'formik';
import Modal from 'react-modal';
import { modalStyles } from '../styles/ModalStyles';
import '../styles/CreateOrUpdateProfile.css';

const CREATE_PROFILE_MUTATION = gql`
    mutation CreateOrUpdateProfile(
        $bio: String
        $location: String
        $website: String
    ) { CreateOrUpdateProfile(
            bio: $bio
            location: $location
            website: $website
        ) {
            id
        }
    }
`
;

interface ProfileValues {
    bio: string,
    location: string,
    website: string,
    avatar: string
}

function CreateOrUpdateProfile() {
    const [mutateCreateOrUpdateProfile] = useMutation(CREATE_PROFILE_MUTATION, { // returns a tuple but we only get one item
        refetchQueries: [{query: PROFILE_QUERY}] // updates the client's cache with fresh data
    });
    const [modalIsOpen, setIsOpen] = useState(false);
    const initialValues : ProfileValues = {
        bio: "",
        location: "",
        website: "",
        avatar: ""
    };
    const openModal = () => {
        setIsOpen(true);
    };
    const closeModal = () => {
        setIsOpen(false);
    }
    const {loading,error,data} = useQuery(PROFILE_QUERY);
    if(loading) return <p>Loading...</p>;
    if(error) return <p>{error.message}</p>;
    const initialValuesForUpdate : ProfileValues = {
        bio: data.Profile.bio,
        location: data.Profile.location,
        website: data.Profile.website,
        avatar: ""
    };
  return (
    <div className='coup-container'>
        {data.Profile ?
        (<button onClick={openModal} className='coup-create-edit-profile-button'>Update profile</button>)
        : (<button onClick={openModal} className='coup-create-edit-profile-button'>Create profile</button>)}
        <Modal isOpen={modalIsOpen} // describes if the modal should be shown
            onRequestClose={closeModal} // function that will be run when the modal is requested to be closed
            contentLabel="Modal" // string for modal label
            style={modalStyles} // styles for the modal
            ariaHideApp={false} // Boolean indicating if the appElement should be hidden
            >
        <div className='coup-formik'>      
            <Formik
                initialValues={data.Profile ? initialValuesForUpdate : initialValues}
                onSubmit={async (values,{setSubmitting}) => {
                    setSubmitting(true);
                    await mutateCreateOrUpdateProfile({ variables: values});
                    setSubmitting(false);
                    closeModal();
                }}>
                    <Form className='coup-form'>
                        <Field name="bio" type="text" as="textarea" placeholder="Bio" className='coup-textarea-field' /><br />
                        <Field name="location" type="text" placeholder="Location" className='coup-field' /><br />
                        <Field name="website" type="text" placeholder="Website" className='coup-field' /><br />
                        <button type="submit" className="coup-button"><span className='coup-span'>{data.Profile ? `Update` : `Create`}</span></button>
                    </Form>
            </Formik>
        </div>
        </Modal>
    </div>
  )
}

export default CreateOrUpdateProfile;
