import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import { useNavigate } from 'react-router-dom';
import '../styles/Profile.css';

export const PROFILE_QUERY = gql`
query Profile {
    Profile {
        id
        userId
        bio
        location
        website
    }
}
`
;

const USER_QUERY = gql`
query me {
    me {
      id
      name
    }
  }
`
;

function Profile() {
    const navigate = useNavigate();
    const {loading,error,data} = useQuery(PROFILE_QUERY);
    const {loading: loading_user, error: error_user, data: data_user} = useQuery(USER_QUERY);
    if(loading) return <p>Loading...</p>;
    if(error) return <p>{error.message}</p>;
    if(loading_user) return <p>Loading...</p>;
    if(error_user) return <p>{error_user.message}</p>;
  return (
    <div className='profile-container'>(remember about cases when profile data do not exist)
        <div className='profile-wrapper'>
            <div className='profile-left'>
                leftNavBar
            </div>
            <div className='profile-center'>
                <div className='profile-header-image'>
                    Header image
                </div>
                <div className='profile-profile-image'>
                    Profile image
                </div>
                <div className='profile-create-or-update-profile'>
                    <CreateOrUpdateProfile />
                </div>
                <h3 className='profile-account-name'>{data_user.me.name}</h3>
                <span className='profile-username'>@username</span>
                {data.Profile.bio ? (
                    <p className='profile-p'>
                        {data.Profile.bio}
                    </p>
                ) : null}
                {data.Profile.location ? (
                    <p className='profile-p'>
                        <i id='profile-location-icon' className='fa-solid fa-location-dot'></i>{" "}
                        {data.Profile.location}
                        <span className='profile-tab-space'>
                            {data.Profile.website ? (
                            <span className='profile-p'>
                                <i id='profile-website-icon' className='fas fa-link'></i>{" "}
                                <a className='profile-a' href={data.Profile.website} target='_blank'>{data.Profile.website}</a>
                            </span>
                            ) : null}
                        </span>
                    </p>
                ) : data.Profile.website ? (
                        <p className='profile-p'>
                            <i id='profile-website-icon' className='fas fa-link'></i>{" "}
                            <a className='profile-a' href={data.Profile.website} target='_blank'>{data.Profile.website}</a>
                        </p>
                    ) : null
                }
                <p className='profile-join-date'>
                    <i id='profile-calendar-icon' className="fa-solid fa-calendar-days"></i>{' '}
                    Joined May 2015
                </p>
                <p className='profile-following-followers'>
                    <span className='profile-span-bold'>0</span> Following <span className='profile-span-bold'>0</span> Followers
                </p>
            </div>
            <div className='profile-right'>
                Trending Accounts
            </div>
        </div>
    </div>
  )
}

export default Profile;
