import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import { useNavigate } from 'react-router-dom';
import '../styles/Profile.css';

export const PROFILE_QUERY = gql`
query Profile {
    Profile {
        id
        userId
        bio
        location
        website
    }
}
`
;

const USER_QUERY = gql`
query me {
    me {
      id
      name
    }
  }
`
;

function Profile() {
    const navigate = useNavigate();
    const {loading,error,data} = useQuery(PROFILE_QUERY);
    const {loading: loading_user, error: error_user, data: data_user} = useQuery(USER_QUERY);
    if(loading) return <p>Loading...</p>;
    if(error) return <p>{error.message}</p>;
    if(loading_user) return <p>Loading...</p>;
    if(error_user) return <p>{error_user.message}</p>;
  return (
    /*<div className="container">
        <h1>Profile</h1>
        <CreateOrUpdateProfile />
        {data.Profile ?
        (<>
            <p>{data.Profile.id}</p>
            <p>{data.Profile.userId}</p>
            <p>{data.Profile.bio}</p>
            <p>{data.Profile.location}</p>
            <p>{data.Profile.website}</p>
        </>)
        : (<p>No profile data</p>)}
        </div>*/
    <div className='profile-container'>
        <div className='profile-wrapper'>
            <div className='profile-left'>
                leftNavBar
            </div>
            <div className='profile-center'>
                <span className='profile-back-arrow' onClick={() => navigate(-1)}>
                    <i className='fa fa-arrow-left' aria-hidden="true"></i>
                </span>
                <div className='profile-avatar'>
                    Avatar
                </div>
                <div className='profile-create-or-update-profile'>
                    <CreateOrUpdateProfile />
                </div>
                <h3 className='profile-name'>{data_user.me.name}</h3>
                {data.Profile ? (
                    <p>
                        <i className='fas fa-link'></i>{" "}
                        <a className='profile-a' href={data.Profile.website} target='_blank'>{data.Profile.website}</a>
                    </p>
                ) : null}
                <div className='profile-followers'>
                    Following
                    <p>followers</p>
                </div>
                <div className='profile-liked-tweets'>
                    Liked tweets
                </div>
            </div>
            <div className='profile-right'>
                Popular Tweets
            </div>
        </div>
    </div>
  )
}

export default Profile;
