import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import { Link, useNavigate } from 'react-router-dom';

export const PROFILE_QUERY = gql`
query Profile {
    Profile {
        id
        userId
        bio
        location
        website
    }
}
`
;

const USER_QUERY = gql`
query me {
    me {
      id
      name
    }
  }
`
;

function Profile() {
    const navigate = useNavigate();
    const {loading,error,data} = useQuery(PROFILE_QUERY);
    const {loading: loading_user, error: error_user, data: data_user} = useQuery(USER_QUERY);
    if(loading) return <p>Loading...</p>;
    if(error) return <p>{error.message}</p>;
    if(loading_user) return <p>Loading...</p>;
    if(error_user) return <p>{error_user.message}</p>;
  return (
    /*<div className="container">
        <h1>Profile</h1>
        <CreateOrUpdateProfile />
        {data.Profile ?
        (<>
            <p>{data.Profile.id}</p>
            <p>{data.Profile.userId}</p>
            <p>{data.Profile.bio}</p>
            <p>{data.Profile.location}</p>
            <p>{data.Profile.website}</p>
        </>)
        : (<p>No profile data</p>)}
        </div>*/
    <>
        <div className='profile-primary'>
            <div className='profile-left'>
                leftNavBar
            </div>
            <div className='profile-profile'>
                <div className='profile-profile-info'>
                    <div className='profile-profile-head'>
                        <span className='profile-back-arrow' onClick={() => navigate(-1)}>
                            <i className='fa fa-arrow-left' aria-hidden="true"></i>
                        </span>
                        <span className='profile-nickname'>
                            <h3>{data_user.me.name}</h3>
                        </span>
                    </div>
                    <div className='profile-avatar'>

                    </div>
                    <div className='profile-make-profile'>
                        <CreateOrUpdateProfile />
                    </div>
                    <h3 className='profile-name'>{data_user.me.name}</h3>
                    {data.Profile ? (
                        <p>
                            <i className='fas fa-link'></i>{" "}
                            <Link currentPath='/o' to={{ pathname: data.Profile.website}} target="_blank">
                                {data.Profile.website}
                            </Link>k
                        </p>
                    ) : null}
                    <div className='profile-followers'>
                        <p>followers</p>
                    </div>
                </div>
            </div>
        </div>
    </>
  )
}

export default Profile;
