export const modalStyles = {
  content: {
    top: '50%', // positions the modal in the half of the page along the Y axis
    left: '50%', // positions the modal in the half of the page along the X axis
    right: 'auto', // allows to adjust the size of the modal for its content
    bottom: 'auto', // allows to adjust the size of the modal for its content
    transform: 'translate(-50%,-50%)', // repositions the modal to the center of the page
    borderRadius: "4px",
    marginRight: "-50%",
    minWidth: "30em", // 30 * font size
    minHeight: "6em",
  },
  overlay: {
    background: "#9e9e9e69",
  },
}
