export const modalStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto', // allows for adjusting the size of the modal for its content
    bottom: 'auto',
    transform: 'translate(-50%,-50%)', // repositions the modal to the center of the website
    borderRadius: "25px",
  },
  overlay: {
    background: "#9e9e9e69",
  },
}
