import React from "react";
import { gql, useQuery } from '@apollo/client';
import { Navigate  } from "react-router-dom";

const IS_LOGGED_IN = gql`
{
    me {
        id
    }
}
`;

interface Properties {
    children?: React.ReactNode // if this prop exists
}

export default function IsAuthenticated({children} : Properties){ // the whole object is of type "Properties" but only "children" properties will be extracted
    const {data} = useQuery(IS_LOGGED_IN); // loading state, error state and data returned by the query
    if(loading) return <p>Loading...</p>;
    if(error) return <p>{error.message}</p>;
    if(!data.me){ // in case of no data
        return <Navigate to="/home" />;
    }
    return <>{children}</>;
}
