import { Formik, Form, Field } from 'formik';
import { gql, useMutation } from '@apollo/client';

export default function AddMovie() {
  const MUTATION = gql`
    mutation($title: String!) {
      addMovie(title: $title) {
        id
      }
    }
    `;
  const [mutateFunction] = useMutation(MUTATION);
  let accountFormInitialValues = {
    title: '',
  };
  return (
    <div>
      add movie
      <Formik initialValues={accountFormInitialValues} onSubmit={async (values,{setSubmitting}) => {
          setSubmitting(true);
          await mutateFunction({variables: values});
          setSubmitting(false);
      }}>
        <Form>
          <Field name='title' type="text"></Field>
          <button type="submit">add</button>
        </Form>
      </Formik>
    </div>
  )
}
