import { gql, useMutation, useQuery } from '@apollo/client';
import { PROFILE_USER_QUERY } from '../sites/Profile';
import { useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import Modal from 'react-modal';
import { modalStyles } from '../styles/ModalStyles';
import '../styles/CreateOrUpdateProfile.css';
import * as yup from 'yup';
import { useNavigate } from 'react-router-dom';

const CREATE_PROFILE_MUTATION = gql`
    mutation CreateOrUpdateProfile(
        $bio: String
        $location: String
        $website: String
    ) { CreateOrUpdateProfile(
            bio: $bio
            location: $location
            website: $website
        ) {
            id
        }
    }
`
;

interface ProfileValues {
    bio: string,
    location: string,
    website: string,
    avatar: string
}

export let profileImageBase64String = '';

function CreateOrUpdateProfile() {
    const [mutateCreateOrUpdateProfile] = useMutation(CREATE_PROFILE_MUTATION, { // returns a tuple but we only get one item
        refetchQueries: [{query: PROFILE_USER_QUERY}] // updates the client's cache with fresh data
    });
    const [modalIsOpen, setIsOpen] = useState(false);
    const initialValues : ProfileValues = {
        bio: "",
        location: "",
        website: "",
        avatar: ""
    };
    const openModal = () => {
        setIsOpen(true);
    };
    const closeModal = async() => {
        setFile('');
        setIsProfileImageUploadError(false);
        try {
            const response = await fetch('http://localhost:4001/downloadProfileImage', { // a promise that was returned by fetch() is resolved with the Response object which represents a response to a request
                method: 'POST',
                body: 'user_id_' + data?.me.id + '_profile_image'
            });
            const responseData = await response.text(); // "text()" returns the "Promise<string>" type
            profileImageBase64String = 'data:;base64,' + responseData;
        }
        catch(error) {
            console.log('Error downloading the profile image',error);
        }
        setIsOpen(false);
    }
    const [file, setFile] = useState(''); // empty string so that TypeScript won't infer the type to "undefined"
    const [isProfileImageUploadError, setIsProfileImageUploadError] = useState(false);
    const allowedTypes = ['image/jpeg','image/png','image/webp'];
    let profileImagePath = '';
    function handleChange(e: React.ChangeEvent<HTMLInputElement>) {
        if(e.target.files) { // e.target.files can be null when changing a file so if-else is necessary
            if(e.target.files.length !== 0) { // prevents the error while canceling the file explorer while overwriting
                const selectedFile = e.target.files[0];
                if(allowedTypes.includes(selectedFile?.type)){
                    setIsProfileImageUploadError(false);
                    profileImagePath = URL.createObjectURL(e.target.files[0]);
                    setFile(profileImagePath);
                }
                else {
                    setIsProfileImageUploadError(true);
                }
            }
        }
    }
    async function imageToBase64String(file:string) {
        const imageToBase64 = require('image-to-base64/browser');
        try {
            const imageToBase64String: string = await imageToBase64(file);
            return imageToBase64String;
        }
        catch (error) {
            console.error(error);
        }
        return '';
    }
    async function handleUpload(){
        if(data.me && data.me.id && file){
            const base64String = await imageToBase64String(file);
            const formData = new FormData();
            formData.append('user_id_' + data.me.id + '_profile_image', base64String);
            fetch('http://localhost:4001/uploadProfileImage',{
                method: 'POST',
                body: formData
            })
            .catch(function(error) { console.log('Error uploading the profile image');});
        }
    }
    const {loading,error,data} = useQuery(PROFILE_USER_QUERY);
    if(loading) return '';
    if(error) return <p>{error.message}</p>;
    let initialValuesForUpdate : ProfileValues = {
        bio: '',
        location: '',
        website: '',
        avatar: ''
    };
    if(data.Profile) {
        initialValuesForUpdate = {
            bio: data.Profile.bio,
            location: data.Profile.location,
            website: data.Profile.website,
            avatar: ""
        };
    }
    const websiteRegex = /^(https:\/\/|http:\/\/).*/g;
    const locationRegex = /^[A-Z][a-zA-Z ]*, [A-Z][a-zA-Z ]*$/g;
    const validationSchema = yup.object({
        website: yup.string().matches(websiteRegex,'Wrong format. Example: https://www.google.com'),
        location: yup.string().matches(locationRegex,'Wrong format. Example: USA, Miami')
    });
  return (
    <div className='coup-container'>
        {data.Profile ?
        (<button onClick={openModal} className='coup-create-edit-profile-button'>Update profile</button>)
        : (<button onClick={openModal} className='coup-create-edit-profile-button'>Create profile</button>)}
        <Modal isOpen={modalIsOpen} // describes if the modal should be shown
            onRequestClose={closeModal} // function that will be run when the modal is requested to be closed
            contentLabel="Modal" // string for modal label
            style={modalStyles} // styles for the modal
            ariaHideApp={false} // Boolean indicating if the appElement should be hidden
            >
        <div className='coup-formik'>
            <Formik
                initialValues={data.Profile ? initialValuesForUpdate : initialValues}
                validationSchema={validationSchema}
                onSubmit={async (values,{setSubmitting}) => {
                    setSubmitting(true);
                    await mutateCreateOrUpdateProfile({ variables: values});
                    handleUpload();
                    setSubmitting(false);
                    closeModal();
                }}>
                    <Form className='coup-form'>
                        <div className='coup-profile-image-upload'>
                            <label className='coup-label-profile-image' htmlFor='coup-input-profile-image'>
                                <i className='fa-solid fa-upload'></i>{' '}
                                Upload profile picture
                            </label>
                            <input id='coup-input-profile-image' type='file' name='' onChange={handleChange} />
                            {file ? (
                                <img id='coup-img-profile-image' src={file} />
                            ) : null}
                        </div>
                        <div className='coup-profile-image-upload-error'>
                            {isProfileImageUploadError ? (
                                <span className='coup-profile-image-upload-error-message'>Wrong format. Allowed formats: JPG/JPEG, PNG, WebP</span>
                            ) : null}
                        </div>
                        <Field name="bio" type="text" as="textarea" placeholder="Bio" className='coup-textarea-field' /><br />
                        <Field name="location" type="text" placeholder="Location" className='coup-field' /><br />
                        <ErrorMessage name='location' component='div' className='coup-error-message' />
                        <Field name="website" type="text" placeholder="Website" className='coup-field' /><br />
                        <ErrorMessage name='website' component='div' className='coup-error-message' />
                        <button type="submit" className="coup-button"><span className='coup-span'>{data.Profile ? `Update` : `Create`}</span></button>
                    </Form>
            </Formik>
        </div>
        </Modal>
    </div>
  )
}

export default CreateOrUpdateProfile;
