import React from "react";
import { gql, useMutation } from '@apollo/client';
import { ErrorMessage, Field, Form, Formik } from 'formik'; 
import * as yup from 'yup';
import { Link, useNavigate } from 'react-router-dom';
import { Sign } from "crypto";
import TwitterLogo from '../styles/assets/TwitterLogo.png';
import '../styles/login.css';

const SIGNUP_MUTATION = gql`
mutation signup($name: String, $email: String!, $password: String!){
    signup(name: $name, email: $email, password: $password){
        token
    }
}
`;

interface SignupValues{
    email: string,
    password: string,
    confirmPassword: string,
    name: string
}

function Signup(){
    const navigate = useNavigate();
    const [mutateSignup] = useMutation(SIGNUP_MUTATION); // returns a tuple consisting of a mutate function that you can call at any time to execute the mutation and an object with fields that represent the current status of the mutation's execution (data, loading, etc.)
    const initialValues: SignupValues = {
        email: '',
        password: '',
        confirmPassword: '',
        name: ''
    }
    const validationSchema = yup.object({
        email: yup.string().email("Invalid email address").required("Email required"), // defines a string schema
        password: yup.string().max(20, '20 characters or less').required("Password required"),
        confirmPassword: yup.string().oneOf([yup.ref("password")],"Passwords must match"), // if password field has null value
        name: yup.string().max(15,"15 characters or less").required("Name required.")
    })
    return(
        <div>
            <img src={TwitterLogo} alt='Twitter logo' className="logo"></img>
            <h3>Sign up</h3>
            <Formik
                initialValues={initialValues}
                validationSchema={validationSchema}
                onSubmit={async (values,{setSubmitting}) => {
                    setSubmitting(true);
                    const response = await mutateSignup({ variables: values});
                    localStorage.setItem("token", response.data.signup.token);
                    setSubmitting(false);
                    navigate('/users');
                }}>
                    <Form>
                        <Field name="email" type="text" placeholder="email" />
                        <ErrorMessage name="email" component={'div'} />
                        <Field name="name" type="text" placeholder="name" />
                        <ErrorMessage name="name" component={'div'} />
                        <Field name="password" type="password" placeholder="password" />
                        <ErrorMessage name="password" component={'div'} /> {/* component for rendering a message */}
                        <Field name="confirmPassword" type="password" placeholder="confirmPassword" />
                        <ErrorMessage name="confirmPassword" component={'div'} />
                        <button type="submit" className="login-button"><span>Sign up</span></button>
                    </Form>
            </Formik>
            <div className="register">
                <br />
                <span className="question">Already have an account?</span>
                <br />
                <Link to="/login">Log in</Link>
            </div>
        </div>
    )
}

export default Signup;
