import { gql, useQuery } from '@apollo/client';
import CreateOrUpdateProfile from '../components/CreateOrUpdateProfile';
import '../styles/Profile.css';

export const PROFILE_USER_QUERY = gql`
query {
    Profile {
      id
      userId
      bio
      location
      website
    }
    me {
      id
      name
      createdAt
    }
}
`
;

function Profile() {
    const {loading, error, data} = useQuery(PROFILE_USER_QUERY);
    if(loading) return <p>Loading...</p>;
    //if(error) return <p>{error.message}</p>;
    //if(data.me && data.me.id) {
        fetch('http://localhost:4001/downloadProfileImage', {
            method: 'POST',
            body: data.me.id
        })
        .catch(function(error) { console.log('Error downloading the profile picture');});
    //}
  return (
    <div className='profile-container'>
        <div className='profile-wrapper'>
            <div className='profile-left'>
                leftNavBar
            </div>
            <div className='profile-center'>
                <div className='profile-profile-image'>
                    {data.Profile && data.Profile.profileImage ? (
                        <img className='profile-profile-img-profile-image' src={data.Profile.profileImage} alt='Profile image'></img>
                    ) : <i id='profile-i-profile-image' className="fa-solid fa-circle-user"></i>}
                </div>
                <div className='profile-create-or-update-profile'>
                    <CreateOrUpdateProfile />
                </div>
                {data.me ? (
                    <h3 className='profile-account-name'>{data.me.name}</h3>
                ) : null}
                <span className='profile-account-username'>@accountusername</span>
                {data.Profile && data.Profile.bio ? (
                    <p className='profile-p'>
                        {data.Profile.bio}
                    </p>
                ) : null}
                {data.Profile && data.Profile.location ? (
                    <p className='profile-p'>
                        <i id='profile-location-icon' className='fa-solid fa-location-dot'></i>{" "}
                        {data.Profile.location}
                        <span className='profile-tab-space'>
                            {data.Profile && data.Profile.website ? (
                            <span className='profile-p'>
                                <i id='profile-website-icon' className='fas fa-link'></i>{" "}
                                <a href={data.Profile.website} target='_blank' className='profile-a'>{data.Profile.website}</a>
                            </span>
                            ) : null}
                        </span>
                    </p>
                ) : data.Profile && data.Profile.website ? (
                        <p className='profile-p'>
                            <i id='profile-website-icon' className='fas fa-link'></i>{" "}
                            <a href={data.Profile.website} target='_blank' className='profile-a'>{data.Profile.website}</a>
                        </p>
                    ) : null
                }
                {data.me && data.me.createdAt ? (
                    <p className='profile-join-date'>
                        <i id='profile-calendar-icon' className="fa-solid fa-calendar-days"></i>{' '}
                        Joined {data.me.createdAt}
                    </p>
                ) : null}
                <p className='profile-following-followers'>
                    <span className='profile-span-bold'>0</span> Following <span className='profile-span-bold'>0</span> Followers
                </p>
            </div>
            <div className='profile-right'>
                Trending Accounts
            </div>
        </div>
    </div>
  )
}

export default Profile;
