import React from 'react'
import { gql, useMutation } from '@apollo/client';
import { ME_QUERY } from '../sites/Profile';
import { useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import Modal from 'react-modal';
import { customStyles } from '../styles/CustomModalStyles';

const CREATE_PROFILE_MUTATION = gql`
    mutation createProfile()
        $bio: String
        $location: String
        $website: String
        $avatar: String
    ) {
        createProfile (
            bio: $bio
            location: $location
            website: $website
            avatar: $avatar
        ) {
            id
        }
    }
`
;

interface ProfileValues {
    bio: string,
    location: string,
    website: string,
    avatar: string
}

function CreateProfile() {
    const [createProfile] = useMutation(CREATE_PROFILE_MUTATION, { // returns a tuple but we only get one item
        refetchQueries: [{query: ME_QUERY}] // updates the client's cache with fresh data
    });
    const [modalIsOpen, setIsOpen] = useState(false);
    const initialValues : ProfileValues = {
        bio: "",
        location: "",
        website: "",
        avatar: ""
    };
    const openModal = () => {
        setIsOpen(true);
    };
    const closeModal = () => {
        setIsOpen(false);
    }
  return (
    <div>
        
    </div>
  )
}

export default CreateProfile;
